function arcAjaxRequest(x, c, d, a) {
    c["arcsid"] = arcsid;
    $.ajax({
        url: window.location.protocol + "//" + window.location.host + "/" + x,
        dataType: "json",
        type: "post",
        contentType: "application/x-www-form-urlencoded",
        data: c,
        complete: function (e) {
            if (typeof (d) == "function") {
                d(e)
            }
        },
        success: function (e) {
            if (typeof (a) == "function") {
                a(e)
            }
        }
    })
}

function updateStatus(d) {
    $.ajax({
        url: "/",
        dataType: "json",
        type: "post",
        cache: false,
        contentType: "application/x-www-form-urlencoded",
        data: {action: "getarcsystemmessages"},
        success: function (e) {
            var b = jQuery.parseJSON(JSON.stringify(e));
            $("#" + d).append(b.data);        
        }})
}

function arcGetStatus(f) {
    $.ajax({
        url: "/",
        dataType: "json",
        type: "post",
        cache: false,
        contentType: "application/x-www-form-urlencoded",
        data: {action: "getarcstatusmessages"},
        success: function (e) {
            f(e);        
        }})
}

function arcGetJson(data) {
    return jQuery.parseJSON(JSON.stringify(data));
}