function arcAjaxRequest(x, c, d, a) {
    if (typeof arcsid != 'undefined') {
        c["arcsid"] = arcsid;
    }
    $.ajax({
        url: window.location.protocol + "//" + window.location.host + "/" + x,
        dataType: "json",
        type: "post",
        contentType: "application/x-www-form-urlencoded",
        data: c,
        complete: function (e) {
            if (typeof (d) == "function") {
                d(e)
            }
        },
        success: function (e) {
            if (typeof (a) == "function") {
                a(e)
            }
        }
    })
}

function updateStatus(d, a) {
    $.ajax({
        url: "/",
        dataType: "json",
        type: "post",
        cache: false,
        contentType: "application/x-www-form-urlencoded",
        data: {action: "getarcsystemmessages"},
        success: function (e) {
            var b = jQuery.parseJSON(JSON.stringify(e));
            $("#" + d).append(b.data);
            $("html,body").animate({scrollTop: $("#" + d).offset().top}, "slow");
            var c = $("#" + d + " div[id]").map(function () {
                return this.id
            }).get();
            c.forEach(function (f) {
                $("#" + f).fadeIn(5000);
                window.setTimeout(function () {
                    $("#" + f).fadeTo(300, 0.5).slideUp(2000, function () {
                        $("#" + f).alert("close")
                    }
                    )
                }, 5000)
            });
            if (typeof (a) == "function") {
                a(b)
            }
        }})
}
;