function updateStatus(status, data) {
    if (status != "danger" && status != "success" && status != "warning") {
        console.log("Invalid status: " + status);
        return;
    }
    $("#status").html(data);
    $("#status").css("display", "block");
    $("#status").attr("class", "alert alert-" + status);
    $('html,body').animate({scrollTop: $("#status").offset().top}, 'slow');
    window.setTimeout(function () {
        $("#status").fadeTo(500, 0).slideUp(500, function () {
            $("#status").css("display", "none");
            $("#status").css("opacity", "1");
        });
    }, 3000);
}