function updateStatus(a) {
    $.ajax({
        url: "/",
        dataType: "json",
        type: "post",
        contentType: "application/x-www-form-urlencoded",
        data: {action: "getarcsystemmessages"},
        success: function (c) {
            var b = jQuery.parseJSON(JSON.stringify(c));
            $("#" + a).html(b.data);
            $("html,body").animate({scrollTop: $("#" + a).offset().top}, "slow");
            if (typeof(updateStatusCallback) == "function") {
                updateStatusCallback(b);
            }
        }});
}
;