function updateStatus(d,a){$.ajax({url:"/",dataType:"json",type:"post",contentType:"application/x-www-form-urlencoded",data:{action:"getarcsystemmessages"},success:function(c){var b=jQuery.parseJSON(JSON.stringify(c));$("#"+d).html(b.data);$("html,body").animate({scrollTop:$("#"+d).offset().top},"slow");window.setTimeout(function(){$("#"+d).fadeTo(500,0).slideUp(500,function(){$(this).remove()})},5000);if(typeof(a)=="function"){a(b)}}})};