function updateStatus(c,b){$.ajax({url:"/",dataType:"json",type:"post",contentType:"application/x-www-form-urlencoded",data:{action:"getarcsystemmessages"},success:function(d){var a=jQuery.parseJSON(JSON.stringify(d));$("#"+c).html(a.data);$("html,body").animate({scrollTop:$("#"+c).offset().top},"slow");window.setTimeout(function(){$("#"+c).fadeTo(500,0).slideUp(500,function(){$(this).removeAttr("style");$(this).html("")})},5000);if(typeof(b)=="function"){b(a)}}})};