function updateStatus(c, b) {
    $.ajax({url: "/", dataType: "json", type: "post", cache: false, contentType: "application/x-www-form-urlencoded", data: {action: "getarcsystemmessages"}, success: function (d) {
            var a = jQuery.parseJSON(JSON.stringify(d));
            $("#" + c).append(a.data);
            $("html,body").animate({scrollTop: $("#" + c).offset().top}, "slow");

            var IDs = $("#" + c + " div[id]") // find spans with ID attribute
                    .map(function () {
                        return this.id;
                    }) // convert to set of IDs
                    .get(); // convert to instance of Array (optional)

            IDs.forEach(function (entry) {
                $("#" + entry).fadeIn(5000);
                window.setTimeout(function () {
                    // closing the popup
                    $("#" + entry).fadeTo(300, 0.5).slideUp(2000, function () {
                        $("#" + entry).alert('close');
                    });
                }, 5000);
            });

            if (typeof (b) == "function") {
                b(a)
            }
        }})
}
;